using System.Collections; using System.Collections.Generic; using UnityEngine;  public class CameraController : MonoBehaviour {  	private float _currentHeight = 0; 	private float _maxHeight = 0;  	[SerializeField] 	private GameObject player;  	// Use this for initialization 	void Start () {  	}  	// Update is called once per frame 	void Update () { 		_currentHeight = player.transform.position.y - 0.5f;  		if (_currentHeight > _maxHeight) { 			_maxHeight = _currentHeight; 			Debug.Log (true); 		}  		transform.position = new Vector3 (transform.position.x, _maxHeight, transform.position.z); 	} }  